import { useState } from "react"

function Register ( {onSubmit} ) {

  const [formData, setFormData] = useState()
  const [isLanguageEnglish, setIsLanguageEnglish] = useState(true)

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    })
  }

const handleSubmit = (evt) => {
  evt.preventDefault()
  onSubmit(formData)
} 

const handleChangeLanguageEng = () => {
  setIsLanguageEnglish(true)
}

const handleChangeLanguageCn = () => {
  setIsLanguageEnglish(false)
}

  return (
    <div className="auth">
      <div className="auth__container">
      <div className="language">
        <p className={isLanguageEnglish ? 'language__active' : ''} onClick={handleChangeLanguageEng}>EN</p>
        <p className={!isLanguageEnglish ? 'language__active' : ''} onClick={handleChangeLanguageCn}>ä¸­</p>
      </div>
        <p className="auth__title">Sign up</p>
        <form action="" method="post" className="auth__form" onSubmit={handleSubmit}>
          <p className="auth__form__input-block">
            <label for="name">Login</label>
            <input className="auth__form_input" onChange={handleChange} type="text" name="name" id="login" placeholder="Enter your login" required />
          </p>
          <p className="auth__form__input-block">
            <label for="name">Department</label>
            <input className="auth__form_input" onChange={handleChange} type="text" name="department" id="department" placeholder="Enter your department" required />
          </p>
          <p className="auth__form__input-block">
            <label for="name">Password</label>
            <input className="auth__form_input" onChange={handleChange} type="password" name="password" id="password" placeholder="Enter your password" required />
          </p>
          <p className="auth__form__input-block">
            <label for="name">Confirm password</label>
            <input className="auth__form_input" onChange={handleChange} type="password" name="confirmation" id="password" placeholder="Enter your password" required />
          </p>
          <button type="submit" className="auth__button">SIGN UP</button>
        </form>
      </div>
    </div>
  )
}

export default Register
