import { useRef, useState } from "react"

function AddWords ( {onSubmit} ) {

  const [formData, setFormData] = useState()
  const cnRef = useRef()
  const engRef = useRef()
  const exRef = useRef()

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    })
  }

  const clearInputs = () => {
    cnRef.current.value = ''
    engRef.current.value = ''
    exRef.current.value = ''
  }

  const handleSubmit = (evt) => {
    evt.preventDefault()
    onSubmit(formData)
    clearInputs()
  }

  return (
    <div className="add-word">
      <div className="add-word__container">
        <p className="add-word__title">Add new word</p>
        <form action="" method="post" className="add-word__form" onSubmit={handleSubmit} >
          <p className="add-word__form__input-block">
            <label for="department">Department</label>
            <input type="text" onChange={handleChange} name="department" id="department" placeholder="Enter department name" required />
          </p>
          <p className="add-word__form__input-block">
            <label for="lesson">Lesson</label>
            <input type="text" onChange={handleChange} name="lesson" id="lesson" placeholder="Enter lesson subject" required />
          </p>
          <p className="add-word__form__input-block">
            <label for="cn">Word in Chinese</label>
            <input ref={cnRef} type="text" onChange={handleChange} name="cn" id="cn" placeholder="Enter word in Chinese" required />
          </p>
          <p className="add-word__form__input-block">
            <label for="eng">Word in English</label>
            <input ref={engRef} type="text" onChange={handleChange} name="eng" id="eng" placeholder="Enter word in English" required />
          </p>
          <p className="add-word__form__input-block">
            <label for="example">Example</label>
            <input  ref={exRef} type="text" onChange={handleChange} name="example" id="example" placeholder="Enter example" required />
          </p>
          <button type="submit" className="add-word__button">Add new word</button>
        </form>
      </div>
    </div>
  )
}

export default AddWords
