import { useRef } from "react";
import { useNavigate, useLocation } from "react-router-dom";

function Card ( {title} ) {
  const navigate = useNavigate()
  const location = useLocation()
  const currentPath = location.pathname
  const MAINPAGE = '/'
  const titleRef = useRef()
  const cardRef = useRef()


  function handleClick() {
    if (currentPath === MAINPAGE) {
      navigate(`/${title}`)
    } else {
      navigate('/lesson', {state:{lesson:title}})
    }
  }

  cardRef.current.addEventListener('mouseenter', () => {
    titleRef.current.classList.add('card__title_active')
  })
  cardRef.current.addEventListener('mouseleave', () => {
    titleRef.current.classList.remove('card__title_active')
  })


  return (
    <div className="card" onClick={handleClick} ref={cardRef}>
      <h2 ref={titleRef}>{title}</h2>
    </div>
  )
}

export default Card;
