import './index.css'
import React, { useEffect, useState } from 'react'
import { Routes, Route, useNavigate } from 'react-router-dom'
import { data } from './vendor/constants';
import Lesson from './components/Lesson';
import Section from './components/Section';
import Header from './components/Header';
import Login from './components/Login';
import Register from './components/Register';
import AddWords from './components/AddWords';
import MainApi from './utils/MainApi';
import ProtectedRouteElement from './components/ProtectedRoute';


function App() {

  const navigate = useNavigate()
  const deps = data.departments
  const lessons = data.lessons
  const [revers, setRevers] = useState(false)
  const [isLoggedIn, setIsLoggedIn] = useState(true)

  const handleLogin = async (values) => {
    console.log('login test', values)
    const { name, password } = values
    try {
      await MainApi.login(name, password)
      setIsLoggedIn(true)
      localStorage.setItem('isLoggedIn', true)
    } catch (err) {
      console.log(err)
    }
  }

  const handleRegister = async (values) => {
    const { name, department, password, confirmation} = values
    try {
      await MainApi.register(name, department, password, confirmation)
      setIsLoggedIn(true)
      localStorage.setItem('isLoggedIn', true)
    } catch (err) {
      console.log(err)
    }
  }

  const handleAddWord = (department, lesson, cn, eng, example) => {
    console.log(department, lesson, cn, eng, example)
  }

  return (
    <div className='page'>
      <Header isClickedStatus={revers} isClicked={setRevers}/>
      <Routes>

        {/* Main page protected route */}
        <Route path='/' element={
          <ProtectedRouteElement 
            element={Section}
            isLoggedIn={isLoggedIn}
            data={deps}
          />
        }/>

        {/* Add word protected route */}
        {/* <Route path='/add' element={
          <ProtectedRouteElement 
            element={AddWords}
            isLoggedIn={isLoggedIn}
          />
        }/>         */}

        {/* FO protected route */}
        <Route path='/fo' element={
          <ProtectedRouteElement 
            element={Section}
            isLoggedIn={isLoggedIn}
            data={lessons}
            title='FO'
          />
        }/>

        {/* FNB protected route */}
        <Route path='/fnb' element={
          <ProtectedRouteElement 
            element={Section}
            isLoggedIn={isLoggedIn}
            data={lessons}
            title='FNB'
          />
        }/>

        {/* Lesson protected route */}
        <Route path='/lesson' element={
          <ProtectedRouteElement 
            element={Lesson}
            isLoggedIn={isLoggedIn}
            data={lessons}
            isReversed={revers}
          />
        }/>

        <Route path='/signin' element={<Login onSubmit={handleLogin}/>} />
        <Route path='/signup' element={<Register onSubmit={handleRegister}/>} />
        <Route path='/add' element={<AddWords onSubmit={handleAddWord}/>} />
      </Routes>
    </div>
  );
}

export default App;
