import { useState } from "react"
import { login } from "../utils/languages";

function Login ( {onSubmit} ) {

  const [formData, setFormData] = useState()

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    })
  }

  const handleSubmit = (evt) => {
    evt.preventDefault()
    onSubmit(formData)
  }

  const [language, setLanguage] = useState('eng')

  return (
    <div className="auth">
      <div className="auth__container">
        <p className="auth__title">{login.cn.title}</p>
        <form action="" method="post" className="auth__form" >
          <p className="auth__form__input-block">
            <label>Login</label>
            <input className="auth__form_input" onChange={handleChange} type="text" name="name" id="login" placeholder="Enter your login" required />
          </p>
          <p className="auth__form__input-block">
            <label>Password</label>
            <input className="auth__form_input" onChange={handleChange} type="password" name="password" id="password" placeholder="Enter your password" required />
          </p>
          <button type="submit" className="auth__button" onClick={handleSubmit}>SIGN IN</button>
        </form>
      </div>
    </div>
  )
}

export default Login
