import { Link, useLocation,  } from "react-router-dom"
import home from '../images/home.png'
import back from '../images/back.png'
import revers from '../images/revers.png'

function Header ( {reversStatus, handleReverse} ) {
  const location = useLocation()

  const goBack = () => {
      window.history.back()
  }

  const handleClickRevers = () => {
    if (reversStatus) {
      handleReverse(true)
    } else {
      handleReverse(false)
    }
  }

  return(
    <header className="header">
      <Link to='/' ><img src={home} className="header__img"/></Link>
      <img src={back} className={location.pathname === '/' ? 'hidden' : 'header__img' } onClick={goBack}/>
      <img src={revers} className={location.pathname === '/lesson' ? 'header__img' : 'hidden'} onClick={handleClickRevers}/>
    </header>
  )
}

export default Header
