import { useEffect, useRef, useState } from "react";
import next from '../images/next.png'
import previous from '../images/previous.png'
import restart from '../images/restart.png'

function Word ( {word} ) {
  const [currentWordIndex, setCurrentWordIndex] = useState(0)
  const [nextButton, setNextButton] = useState(next)
  const [hidden, setHidden] = useState('hidden')
  const nextButtonRef = useRef()
  const prevButtonref = useRef()
  const wordsRef = useRef()

  function handleClickNext() {
    if (currentWordIndex < word.length - 1) {
      setCurrentWordIndex(currentWordIndex + 1)
    } else if (currentWordIndex === word.length - 1) {
      setCurrentWordIndex(0)
      setNextButton(next)
    }
    setHidden('hidden')
  }

  function handleClickPrevious() {
    if (currentWordIndex > 0) {
      setCurrentWordIndex(currentWordIndex - 1)
    }
    setHidden('hidden')
  }

  function handleShowContent() {
    setHidden('')
  }

  function handleKeyboardManager(e) {
    e.preventDefault()
    if (e.keyCode === 32) {
      handleShowContent()
    }
    if (e.keyCode === 37) {
      handleClickPrevious()
    }
    if (e.keyCode === 39) {
      handleClickNext()
    }
  }

  useEffect(() => {
    console.log(currentWordIndex, word.length)
    if (currentWordIndex === word.length - 1) {
      setNextButton(restart)
    } else if (currentWordIndex === 0) {
      prevButtonref.current.classList.add('hidden')
    } else {
      setNextButton(next)
      nextButtonRef.current.classList.remove('hidden')
      prevButtonref.current.classList.remove('hidden')
    }
  },[currentWordIndex])

  useEffect(() => {
    if (wordsRef) {
      wordsRef.current.setAttribute('tabIndex', 0);
      wordsRef.current.addEventListener('keydown', handleKeyboardManager)
    }
    return () => {
      if (wordsRef) {
        wordsRef.current.removeEventListener('keydown', handleKeyboardManager)
      }
    }
  }, [handleKeyboardManager])

  return (
    <div className="word" ref={wordsRef}>
      <div className="word__button" onClick={handleClickPrevious}>
        <img className="word__button_img" src={previous} ref={prevButtonref} onClick={handleClickPrevious}></img>
      </div>

      <div className="word__content" onClick={handleShowContent}>
        <p className="word__title">{word[currentWordIndex].word}</p>
        <div className={hidden}>
          <p className="word__translate">{word[currentWordIndex].translate}</p>
          <p className="word__example">{word[currentWordIndex].example}</p>
        </div>
      </div>

      <div className="word__button" onClick={handleClickNext}>
        <img className="word__button_img" src={nextButton} ref={nextButtonRef} ></img>
      </div>
    </div>
  )
}

export default Word;
