import { Link, useLocation,  } from "react-router-dom"
import home from '../images/home.png'
import back from '../images/back.png'
import revers from '../images/revers.png'
import { useEffect, useRef } from "react"

function Header () {
  const backButtonRef = useRef()
  const reverseButtonRef = useRef()
  const location = useLocation()

  const goBack = () => {
      window.history.back()
  }

  useEffect(() => {
    // if (location.pathname === '/') {
    //   backButtonRef.current.classList.add('hidden')
    //   reverseButtonRef.current.classList.add('hidden')
    // } else if (location.pathname !== '/' && location.pathname !== '/lesson') {
    //   backButtonRef.current.classList.remove('hidden')
    // } else if (location.pathname === '/lesson') {
    //   reverseButtonRef.current.classList.remove('hidden')
    // }

    if (location.pathname !== '') {
      backButtonRef.current.classList.remove('hidden')
    }
  },[location.pathname])

  return(
    <header className="header">
      <Link to='/' ><img src={home} className="header__img"/></Link>
      <img src={back} className="header__img hidden" onClick={goBack} ref={backButtonRef}/>
      <img src={revers} className="header__img hidden" ref={reverseButtonRef}/>
    </header>
  )
}

export default Header
