import { useState } from "react";

function Word ( {word} ) {
  const [currentWordIndex, setCurrentWordIndex] = useState(0)

  console.log('111', word[currentWordIndex].word)

  function handleClick() {
    setCurrentWordIndex(currentWordIndex + 1)
  }

  return (
    <div className="word">
      <p className="word__title">{word[currentWordIndex].word}</p>
      <p className="word__translate">{word[currentWordIndex].word}</p>
      <button onClick={handleClick}>CLICK</button>
    </div>
  )
}

export default Word;
