import { Link,  } from "react-router-dom"
import home from '../images/home.png'
import back from '../images/back.png'
import { useEffect, useRef } from "react"

function Header () {
  const backButtonRef = useRef()

  const goBack = () => {
    if (window.location.pathname !== '/') {
      window.history.back()
    }
  }

  useEffect(() => {
    if (window.location.pathname === '/') {
      backButtonRef.current.classList.add('hidden')
    } else (
      backButtonRef.current.classList.remove('hidden')
    )
  },[window.location.pathname])

  return(
    <header className="header">
      <Link to='/' ><img src={home} className="header__img"/></Link>
      <img src={back} className="header__img" onClick={goBack} ref={backButtonRef}/>
    </header>
  )
}

export default Header
