import { useState } from "react"

function Login ( {onSubmit} ) {

  const [formData, setFormData] = useState({
    name: '',
    password: ''
  })

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    })
  }

  return (
    <div className="auth">
      <div className="auth__container">
        <p className="auth__title">Sign in</p>
        <form action="" method="post" className="auth__form" >
          <p className="auth__form__input-block">
            <label>Login</label>
            <input className="auth__form_input" type="text" name="login" id="login" placeholder="Enter your login" required />
          </p>
          <p className="auth__form__input-block">
            <label>Password</label>
            <input className="auth__form_input" type="password" name="password" id="password" placeholder="Enter your password" required />
          </p>
          <button type="submit" className="auth__button" onClick={handleSubmit}>SIGN IN</button>
        </form>
      </div>
    </div>
  )
}

export default Login
